import {FormEventHandler, MouseEventHandler, useCallback, useEffect, useState} from 'react';
import styles from './widget.module.css';
import {useGoogle} from './hooks/use-google.ts';

const googleIcon = 'data:image/png;base64,';
const loupeIcon = 'data:image/png;base64,';

export default function GoogleSearchBar() {
    const {
        setSearch,
        setUrl,
        getNextPageUrl,
        doSearch,
        clearResults,
        search,
        results,
        searchResultList,
        loading,
        error
    } = useGoogle();
    const [focused, setFocused] = useState(false);
    const [tempSearch, setTempSearch] = useState('');

    const handleInput = useCallback<
        FormEventHandler<HTMLInputElement>
    >(e => {
        setTempSearch(e.currentTarget.value);
        e.currentTarget.value === '' && setSearch('');
    }, [setSearch]);

    const handleSubmit = useCallback<
        FormEventHandler<HTMLFormElement>
    >((e) => {
        e.preventDefault();

        clearResults();
        setSearch(tempSearch);
    }, [clearResults, setSearch, tempSearch]);

    const handleNext = useCallback<MouseEventHandler<HTMLButtonElement>>(() => {
        setUrl(getNextPageUrl())
    }, [setUrl, getNextPageUrl]);

    const toggleFocus = () => setFocused(f => !f);

    useEffect(() => {
        doSearch();
    }, [search, doSearch]);

    useEffect(() => {
        Object.keys(results).length > 0 && console.log(results);
    }, [results]);

    return (<section className={`
        ${styles.widget} 
        ${searchResultList.length > 0 && search.length > 0 && !error ? styles.runningSearch : ''}
    `}>
        <form onSubmit={handleSubmit}>
            <div className={`
                ${focused ? styles.focused : ''} 
                ${error ? styles.error : ''}
            `}>
                <img src={googleIcon}
                     alt="google logo"
                />

                <input type="search"
                       value={tempSearch}
                       placeholder='Votre recherche ...'
                       onInput={handleInput}
                       onFocus={toggleFocus}
                       onBlur={toggleFocus}
                />

                <button type='submit'>
                    <img src={loupeIcon}
                         alt="loupe logo"
                    />
                </button>
            </div>

            {error && (<div style={{color: 'red'}}>{error}</div>)}
        </form>

        <div>
            <ul>
                {searchResultList.map((item, i) =>
                    (<li key={i}>
                        <a href={item.link}
                           target='_blank'
                        >
                            {item.title}
                        </a>
                    </li>))}
            </ul>
            {loading && (<span>Chargement...</span>)}
        </div>

        {(results.queries?.nextPage?.length ?? 0) >= 1 &&
            (<button className={styles.button}
                     onClick={handleNext}
            >
                Suivant
            </button>)}
    </section>);
}

